#include <iostream>
#include <NTL/ZZ.h>

using namespace std;
using namespace NTL;

ZZ module(ZZ a, ZZ n){
    ZZ r = a-n*(a/n);
    r = r+(r<0)*n;
    return r;
}

void Euclides_clasico(ZZ a, ZZ b){
    ZZ temp_a = a;
    ZZ temp_b = b;
    while (module(temp_a, temp_b) != 0){
        ZZ temp = temp_b;
        temp_b = module(temp_a, temp_b);
        temp_a = temp;
    }
    cout<<temp_b;
}
void Euclides_menor_resto(ZZ a, ZZ b){
    ZZ temp_a = a;
    ZZ temp_b = b;
    while (module(temp_a,temp_b) != 0){
        ZZ temp = abs(temp_b);
        ZZ r1 =  abs(temp_a - temp_b * (temp_a/temp_b+3/2));
        ZZ r2 =  abs(temp_a - temp_b * (temp_a/temp_b+1/2));
        if(r1<r2){
            temp_b = r1;
        }else{
            temp_b = r2;
        }
        temp_a = temp;
    }
    cout<<temp_b<<endl;
}
void binario(ZZ u,ZZ v){
    ZZ t, g, a, b;
    g=1;
    a=abs(u);
    b=abs(v);
    while(a%2==0 && b%2==0){
        a=a/2;
        b=b/2;
        g=2*g;
    }
    while(a!=0){
        if(a%2==0){
            a=a/2;
        }else if(b%2==0){
            b=b/2;
        }else{
            t=abs(a-b)/2;
            if(a>= b){
                a=t;
            }else{
                b=t;
            }
        }
    }
    cout<<g*b;
}
